    description                 varchar(255)                        null comment 'Example: I''m a description',
    fork                        tinyint(1)                          not null comment 'Example: ',
    url                         varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api',
    forks_url                   varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/forks',
