    teams_url                   varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/teams',
    hooks_url                   varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/hooks',
    issue_events_url            varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/issues/events{/number}',
    events_url                  varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/events',
    assignees_url               varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/assignees{/user}',
    branches_url                varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/branches{/branch}',
    tags_url                    varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/tags',
    blobs_url                   varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/git/blobs{/sha}',
    git_tags_url                varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/git/tags{/sha}',
    git_refs_url                varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/git/refs{/sha}',
    trees_url                   varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/git/trees{/sha}',
    statuses_url                varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/statuses/{sha}',
    languages_url               varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/languages',
    stargazers_url              varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/stargazers',
    contributors_url            varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/contributors',
    subscribers_url             varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/subscribers',
    subscription_url            varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/subscription',
    commits_url                 varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/commits{/sha}',
    git_commits_url             varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/git/commits{/sha}',
    comments_url                varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/comments{/number}',
    issue_comment_url           varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/issues/comments{/number}',
    contents_url                varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/contents/{+path}',
    compare_url                 varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/compare/{base}...{head}',
    merges_url                  varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/merges',
    archive_url                 varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/{archive_format}{/ref}',
    downloads_url               varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/downloads',
    issues_url                  varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/issues{/number}',
    pulls_url                   varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/pulls{/number}',
    milestones_url              varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/milestones{/number}',
    notifications_url           varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/notifications{?since,all,participating}',
    labels_url                  varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/labels{/name}',
    releases_url                varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/releases{/id}',
    deployments_url             varchar(255)                        not null comment 'Example: https://api.github.com/repos/mikepsinn/qm-api/deployments',
    pushed_at                   varchar(255)                        not null comment 'Example: 2021-10-24T00:07:07Z',
    git_url                     varchar(255)                        not null comment 'Example: git://github.com/mikepsinn/qm-api.git',
    ssh_url                     varchar(255)                        not null comment 'Example: git@github.com:mikepsinn/qm-api.git',
    clone_url                   varchar(255)                        not null comment 'Example: https://github.com/mikepsinn/qm-api.git',
    svn_url                     varchar(255)                        not null comment 'Example: https://github.com/mikepsinn/qm-api',
    homepage                    varchar(255)                        null comment 'Example: https://quantimo.do',
    size                        int                                 not null comment 'Example: 178451',
    stargazers_count            int                                 not null comment 'Example: 2',
    watchers_count              int                                 not null comment 'Example: 2',
    language                    varchar(255)                        not null comment 'Example: PHP',
    has_issues                  tinyint(1)                          not null comment 'Example: 1',
    has_projects                tinyint(1)                          not null comment 'Example: 1',
    has_downloads               tinyint(1)                          not null comment 'Example: 1',
    has_wiki                    tinyint(1)                          not null comment 'Example: 1',
    has_pages                   tinyint(1)                          not null comment 'Example: ',
    forks_count                 int                                 not null comment 'Example: 0',
    archived                    tinyint(1)                          not null comment 'Example: ',
    disabled                    tinyint(1)                          not null comment 'Example: ',
    open_issues_count           int                                 not null comment 'Example: 89',
    allow_forking               tinyint(1)                          not null comment 'Example: 1',
    is_template                 tinyint(1)                          not null comment 'Example: ',
    topics                      longtext                            not null comment 'Example: [digital-health,health,healthcare-data]',
    visibility                  varchar(255)                        not null comment 'Example: private',
    forks                       int                                 not null comment 'Example: 0',
    open_issues                 int                                 not null comment 'Example: 89',
    watchers                    int                                 not null comment 'Example: 2',
    default_branch              varchar(255)                        not null comment 'Example: develop',
    permissions                 longtext                            not null comment 'Example: {admin:true,maintain:true,push:true,triage:true,pull:true}',
    temp_clone_token            varchar(255)                        not null comment 'Example: AAVNV2SECRETYTBOS7ZI',
    allow_squash_merge          tinyint(1)                          not null comment 'Example: 1',
    allow_merge_commit          tinyint(1)                          not null comment 'Example: 1',
    allow_rebase_merge          tinyint(1)                          not null comment 'Example: 1',
    allow_auto_merge            tinyint(1)                          not null comment 'Example: ',
    delete_branch_on_merge      tinyint(1)                          not null comment 'Example: ',
    network_count               int                                 not null comment 'Example: 0',
    subscribers_count           int                                 not null comment 'Example: 0',
    score                       double                              null comment 'Example: 0',
    mirror_url                  varchar(255)                        null comment 'Example: git@github.com:mikepsinn/qm-api.git',
    license                     varchar(100)                        null comment 'GPL-3.0',
    web_commit_signoff_required tinyint(1)                          null,
    constraint github_repositories_git_url_uindex
        unique (git_url),
    constraint github_repositories_github_repository_id_uindex
        unique (github_repository_id),
    constraint github_repositories_html_url_uindex
        unique (html_url),
    constraint github_repositories_id_uindex
        unique (id),
    constraint github_repositories_ssh_url_uindex
        unique (ssh_url),
    constraint github_repositories_url_uindex
        unique (url),
    constraint github_repositories_oa_clients_client_id_fk
        foreign key (client_id) references quantimodo_test.oa_clients (client_id),
    constraint github_repositories_wp_users_ID_fk
        foreign key (user_id) references quantimodo_test.wp_users (ID)
)
    collate = utf8mb4_unicode_ci;

